package de.teammeet.services.xmpp;

import java.math.BigInteger;
import java.security.InvalidAlgorithmParameterException;
import java.security.InvalidKeyException;
import java.security.KeyFactory;
import java.security.KeyPair;
import java.security.KeyPairGenerator;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.security.PrivateKey;
import java.security.PublicKey;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.X509EncodedKeySpec;

import javax.crypto.BadPaddingException;
import javax.crypto.Cipher;
import javax.crypto.IllegalBlockSizeException;
import javax.crypto.KeyAgreement;
import javax.crypto.NoSuchPaddingException;
import javax.crypto.spec.DHParameterSpec;
import javax.crypto.spec.IvParameterSpec;
import javax.crypto.spec.SecretKeySpec;

import android.util.Log;

public class KeyExchangePartner {

	private static final String CLASS = KeyExchangePartner.class.getSimpleName();

	private static final BigInteger mDHPrime = new BigInteger("");
	private static final BigInteger mDHGenerator = new BigInteger("");
	private static final int mDHExponentSize = 0;

	private final String mName;
	private KeyPair mKeyPair;
	private Cipher mAES;
	private SecretKeySpec mSharedSecretSpec;
	private IvParameterSpec mIVSpec;


	public KeyExchangePartner(String name) {
		mName = name;
		mKeyPair = generateKeyPair();
		mAES = createAESCipher();
		mSharedSecretSpec = null;
	}

	private KeyPair generateKeyPair() {
		KeyPair keyPair = null;
		try {
			KeyPairGenerator keyPairGenerator = KeyPairGenerator.getInstance("DH");
			keyPairGenerator.initialize(new DHParameterSpec(mDHPrime, mDHGenerator, mDHExponentSize));

			keyPair = keyPairGenerator.generateKeyPair();
		} catch (NoSuchAlgorithmException e) {
			//TODO: Inform user via UI
			Log.e(CLASS, String.format("Could not acquire Diffie-Hellman key pair generator " +
									   "to exchange session key with '%s': %s", mName, e.getMessage()));
		} catch (InvalidAlgorithmParameterException e) {
			//TODO: Inform user via UI
			Log.e(CLASS, String.format("Could not initialize key pair generator to exchange " +
									   "session key with '%s': %s", mName, e.getMessage()));
		}
		return keyPair;
	}

	private Cipher createAESCipher() {
		Cipher aes = null;

		try {
			aes = Cipher.getInstance("AES/CBC/PKCS5Padding");
		} catch (NoSuchAlgorithmException e) {
			//TODO: Inform user via UI
			Log.e(CLASS, String.format("Can't get AES cipher: %s", e.getMessage()), e);
		} catch (NoSuchPaddingException e) {
			//TODO: Inform user via UI
			Log.e(CLASS, String.format("Can't pad AES cipher: %s", e.getMessage()), e);
		}

		return aes;
	}

	public String getName() {
		return mName;
	}

	public byte[] getPublicKey() {
		return mKeyPair.getPublic().getEncoded();
	}

	public void calculateSharedSecret(byte[] publicKeyBytes) throws InvalidKeyException {
		Log.d(CLASS, String.format("Generating shared secret for '%s'", mName));

		PrivateKey ownPrivateKey = mKeyPair.getPrivate();
		PublicKey foreignPublicKey = restoreKey(publicKeyBytes);
		KeyAgreement keyAgreement = setupKeyAgreement(ownPrivateKey, foreignPublicKey);

		byte[] sharedSecret = keyAgreement.generateSecret();
		Log.d(CLASS, String.format("Generated '%d' bits shared secret", sharedSecret.length * 8));

		try {
			MessageDigest sha256 = MessageDigest.getInstance("SHA-256");
			byte[] aesKey = sha256.digest(sharedSecret);
			mSharedSecretSpec = new SecretKeySpec(aesKey, "AES");

			MessageDigest md5 = MessageDigest.getInstance("MD5");
			byte[] aesIV = md5.digest(sharedSecret);
			mIVSpec = new IvParameterSpec(aesIV);

		} catch (NoSuchAlgorithmException e) {
			Log.e(CLASS, String.format("Could not instantiate message digest: %s", e.getMessage()));
		}
	}

	private PublicKey restoreKey(byte[] keyBytes) {
		X509EncodedKeySpec x509KeySpec = new X509EncodedKeySpec(keyBytes);
		PublicKey publicKey = null;
		try {
			KeyFactory keyFactory = KeyFactory.getInstance("DH");
			publicKey = keyFactory.generatePublic(x509KeySpec);
		} catch (NoSuchAlgorithmException e) {
			//TODO: Notify user via UI
			Log.e(CLASS, String.format("Could not get Diffie-Hellman key factory: %s",
										e.getMessage()));
		} catch (InvalidKeySpecException e) {
			//TODO: Notify user via UI
			Log.e(CLASS, String.format("Key spec used to decrypt received public key is invalid: %s",
										e.getMessage()));
		}
		return publicKey;
	}

	private KeyAgreement setupKeyAgreement(PrivateKey privKey, PublicKey pubKey) throws InvalidKeyException {
		KeyAgreement keyAgreement = null;
		try {
			keyAgreement = KeyAgreement.getInstance("DH");
			keyAgreement.init(privKey);
			keyAgreement.doPhase(pubKey, true);
		} catch (NoSuchAlgorithmException e) {
			//TODO: Notify user via UI
			Log.e(CLASS, String.format("Could not fetch Diffie-Hellman key agreement: %s",
										e.getMessage()));
		}
		return keyAgreement;
	}

	public byte[] encryptSessionKey(byte[] sessionKey) {

		byte[] encryptedKey = null;

		try {
			mAES.init(Cipher.ENCRYPT_MODE, mSharedSecretSpec, mIVSpec);
			encryptedKey = mAES.doFinal(sessionKey);
		} catch (InvalidKeyException e) {
			Log.e(CLASS, String.format("Can't initialize AES with key: %s", e.getMessage()));
		} catch (InvalidAlgorithmParameterException e) {
			Log.e(CLASS, String.format("Can't initialize AES with IV: %s", e.getMessage()));
		} catch (IllegalBlockSizeException e) {
			Log.e(CLASS, String.format("Can't encrypt session key with bad block size: %s",
										e.getMessage()));
		} catch (BadPaddingException e) {
			Log.e(CLASS, String.format("Can't pad session key: %s", e.getMessage()));
		}

		return encryptedKey;
	}

	public byte[] decryptSessionKey(byte[] encryptedKey) {

		byte[] sessionKey = null;

		try {
			mAES.init(Cipher.DECRYPT_MODE, mSharedSecretSpec, mIVSpec);
			sessionKey = mAES.doFinal(encryptedKey);
		} catch (InvalidKeyException e) {
			Log.e(CLASS, String.format("Can't initialize AES with key: %s", e.getMessage()));
		} catch (InvalidAlgorithmParameterException e) {
			Log.e(CLASS, String.format("Can't initialize AES with IV: %s", e.getMessage()));
		} catch (IllegalBlockSizeException e) {
			Log.e(CLASS, String.format("Can't decrypt session key with bad block size: %s",
										e.getMessage()));
		} catch (BadPaddingException e) {
			Log.e(CLASS, String.format("Can't pad encrypted session key: %s", e.getMessage()));
		}

		return sessionKey;
	}

	@Override
	public String toString() {
		return mName;
	}
}
